<script lang="ts">
	import { authStore } from '$lib/auth';
	import { page } from '$app/stores';
	import { onMount } from 'svelte';

	let isActive = (path: string) => $page.url.pathname === path;
	const secondaryHeaderPrefixes = ['/goals', '/subjects', '/lessons'];
	// These sections keep the neutral header palette; everything else stays primary green
	const isSecondaryHeaderRoute = (pathname: string) =>
		secondaryHeaderPrefixes.some((prefix) => pathname === prefix || pathname.startsWith(`${prefix}/`));
	let usePrimaryGreenHeader = $derived(!isSecondaryHeaderRoute($page.url.pathname));

	// Mobile menu state
	let mobileMenuOpen = $state(false);
	let isMobile = $state(false);

	onMount(() => {
		if (typeof window !== 'undefined') {
			const mediaQuery = window.matchMedia('(max-width: 1023px)');
			isMobile = mediaQuery.matches;
			mediaQuery.addEventListener('change', (e) => {
				isMobile = e.matches;
				if (!isMobile) {
					mobileMenuOpen = false;
				}
			});
		}
	});

	function toggleMobileMenu() {
		mobileMenuOpen = !mobileMenuOpen;
		// Sync with checkbox
		if (typeof document !== 'undefined') {
			const checkbox = document.getElementById('mobile-drawer') as HTMLInputElement;
			if (checkbox) {
				checkbox.checked = mobileMenuOpen;
			}
		}
	}

	function closeMobileMenu() {
		mobileMenuOpen = false;
		// Sync with checkbox
		if (typeof document !== 'undefined') {
			const checkbox = document.getElementById('mobile-drawer') as HTMLInputElement;
			if (checkbox) {
				checkbox.checked = false;
			}
		}
	}
</script>

